~~~java

class Solution {
    public long kthSmallestProduct(int[] nums1, int[] nums2, long k) {
        if (nums1 == null || nums2 == null) {
            return -1;
        }

        long left = (long) -1e10;
        long right = (long) 1e10;
        while (left + 1 < right) {
            long mid = left + (right - left) / 2;
            long count = countSmallerOrEqual(nums1, nums2, mid);
            if (count < k) {
                left = mid + 1;
            } else {
                right = mid;
            }
        }

        return countSmallerOrEqual(nums1, nums2, left) >= k ? left : right;
    }

    private long countSmallerOrEqual(int[] nums1, int[] nums2, long target) {
        long count = 0;

        for (int n1 : nums1) {
            int left = 0, right = nums2.length - 1;

            while (left + 1 < right) {
                int mid = left + (right - left) / 2;
                long prod = (long)n1 * nums2[mid];

                if (n1 >= 0) {
                    if (prod <= target) {
                        left = mid;
                    } else {
                        right = mid - 1;
                    }
                } else {
                    if (prod <= target) {
                        right = mid;
                    } else {
                        left = mid + 1;
                    }
                }
            } 

            long currCount = 0;
            if (n1 >= 0) {
                if ((long) n1 * nums2[right] <= target) {
                    currCount = right + 1;
                } else if ((long) n1 * nums2[left] <= target) {
                    currCount = left + 1;
                } else {
                    currCount = 0;
                }
            } else {
                if ((long) n1 * nums2[left] <= target) {
                    currCount = nums2.length - left;
                } else if ((long) n1 * nums2[right] <= target) {
                    currCount = nums2.length - right;
                } else {
                    currCount = 0;
                }
            }
            count += currCount;
        }

        return count;
    }
}

