#include <iostream>
#include <vector>
#include <chrono>
#include <limits>
#include <algorithm>

using std::vector;

class Solution {
public:
    long long kthSmallestProduct(vector<int>& left, vector<int>& right, long long k) {
      long long min = 0l, max = 0l, mid = 0l;
      if (left[0] > 0) {
        if (right[0] > 0) {
          // 纯正 纯正
          min = static_cast<long long>(left[0]) * static_cast<long long>(right[0]);
          max = static_cast<long long>(left[left.size()-1]) * static_cast<long long>(right[right.size()-1]);
        } else if (right[right.size()-1] <= 0) {
          // 纯正 纯负
          min = static_cast<long long>(left[left.size()-1]) * static_cast<long long>(right[0]);
          max = static_cast<long long>(left[0]) * static_cast<long long>(right[right.size()-1]);
        } else {
          // 纯正 混合
          min = static_cast<long long>(left[left.size()-1]) * static_cast<long long>(right[0]);
          max = static_cast<long long>(left[left.size()-1]) * static_cast<long long>(right[right.size()-1]);
        }
      } else if (left[left.size()-1] <= 0) {
        if (right[0] > 0) {
          // 纯负 纯正
          min = static_cast<long long>(left[0]) * static_cast<long long>(right[right.size()-1]);
          max = static_cast<long long>(left[left.size()-1]) * static_cast<long long>(right[0]);
        } else if (right[right.size()-1] <= 0) {
          // 纯负 纯负
          min = static_cast<long long>(left[left.size()-1]) * static_cast<long long>(right[right.size()-1]);
          max = static_cast<long long>(left[0]) * static_cast<long long>(right[0]);
        } else {
          // 纯负 混合
          min = static_cast<long long>(left[0]) * static_cast<long long>(right[right.size()-1]);
          max = static_cast<long long>(left[0]) * static_cast<long long>(right[0]);
        }
      } else {
        if (right[0] > 0) {
          // 混合 纯正
          min = static_cast<long long>(left[0]) * static_cast<long long>(right[right.size()-1]);
          max = static_cast<long long>(left[left.size()-1]) * static_cast<long long>(right[right.size()-1]);
        } else if (right[right.size()-1] <= 0) {
          // 混合 纯负
          min = static_cast<long long>(left[left.size()-1]) * static_cast<long long>(right[0]);
          max = static_cast<long long>(left[0]) * static_cast<long long>(right[0]);
        } else {
          // 混合 混合
          min = std::min(left[0] * static_cast<long long>(right[right.size()-1]), static_cast<long long>(left[left.size()-1]) * static_cast<long long>(right[0]));
          max = std::max(left[0] * static_cast<long long>(right[0]), static_cast<long long>(left[left.size()-1]) * static_cast<long long>(right[right.size()-1]));
        }
      }

      auto findLastNegative = [](vector<int>& nums) -> const int {
        int i = 0;
        for (; i < nums.size(); i++) {
          if (nums[i] >= 0) {
            return i-1;
          }
        }
        return nums.size()-1;
      };
      auto findFirstPositive = [](vector<int>& nums) -> const int {
        int i = 0;
        for (; i < nums.size(); i++) {
          if (nums[i] > 0) {
            return i;
          }
        }
        return nums.size();
      };

      int lx = findLastNegative(left);
      int ly = findFirstPositive(left);
      int rx = findLastNegative(right);
      int ry = findFirstPositive(right);

      while (true) {
        if (max - min <= 1) {
          if (find(left, right, min, lx, ly, rx, ry) >= k) {
            return min;
          } else {
            return max;
          }
        }

        mid = (min+max)/2;
        if (find(left, right, mid, lx, ly, rx, ry) < k) {
          min = mid;
        } else {
          max = mid;
        }
      }
      return mid;
    }

    long long find(vector<int>& left, vector<int>& right, long long target, int lx, int ly, int rx, int ry) {
      long long k = 0;
      if (target >= 0l) {
        // -8, -4, -2, -1, 0, 1, 2, 4, 8 -> 3, 5
        // -8, -4, -2, -1, 0, 1, 2, 4, 8 -> 3, 5
        k += static_cast<long long>(lx+1)*static_cast<long long>((right.size()-ry));      // left 的负数 * right 的正数
        k += static_cast<long long>(left.size()-ly)*static_cast<long long>((rx+1));       // left 的正数 * right 的负数
        k += static_cast<long long>(ly-lx-1)*static_cast<long long>(right.size());        // left 的零 * right
        k += static_cast<long long>(ry-rx-1)*static_cast<long long>(left.size());         // right 的零 * left
        k -= static_cast<long long>(ly-lx-1)*static_cast<long long>((ry-rx-1));           // left 的零 * right 的零

        int i = 0, j = rx;
        // -8, -4, -2, -1
        // -8, -4, -2, -1
        for (; i <= lx; i++) {
          // j 是从右往左第一个不满足的负数
          while (j >= 0) {
            long long d = static_cast<long long>(left[i]) * static_cast<long long>(right[j]);
            if (d <= target) {
              j--;
            } else {
              break;
            }
          }
          k += static_cast<long long>(rx-j);
        }

        // 1, 2, 4, 8 -> 3,5
        // 1, 2, 4, 8 -> 3,5
        j = right.size()-1;
        for (i = ly; i < left.size(); i++) {
          // j 是从右往左, 最后一个满足的
          while (j >= ry) {
            long long d = static_cast<long long>(left[i]) * static_cast<long long>(right[j]);
            if (d > target) {
              j--;
            } else {
              break;
            }
          }
          k += static_cast<long long>(j-ry+1);
        }
      } else {
        int i = 0, j = ry;
        // -8, -4, -2, -1 -> 3,5
        // 1, 2, 4, 8 -> 3,5
        for (; i <= lx; i++) {
          // j 是从左往右, 第一个满足的
          while (j < right.size()) {
            long long d = static_cast<long long>(left[i]) * static_cast<long long>(right[j]);
            if (d > target) {
              j++;
            } else {
              break;
            }
          }
          k += static_cast<long long>(right.size()-j);
        }

        // 1, 2, 3, 4
        // -8,-4,-2,-1
        j = 0;
        for(i = ly; i < left.size(); i++) {
          // j 从左往右, 第一个不满足的
          while (j <= rx) {
            long long d = static_cast<long long>(left[i]) * static_cast<long long>(right[j]);
            if (d <= target) {
              j++;
            } else {
              break;
            }
          }
          k += static_cast<long long>(j);
        }
      }

      return k;
    }

    int countLess(vector<int>& right, int curr, int target) {
      for (; curr >= 0; curr--) {
        if (right[curr] <= target) {
          return curr;
        }
      }
      return -1;
    }

    int countGreater(vector<int>& right, int curr, int target) {
      for (; curr >= 0; curr--) {
        if (right[curr] < target) {
          return curr+1;
        }
      }
      return right.size();
    }
};

