# Code
```
public class Solution {
    public long KthSmallestProduct(int[] nums1, int[] nums2, long k) {
        var a1 = nums1.Where(x => x < 0).ToArray();
        a1 = rev(inv(a1));
        var a2 = nums1.Where(x => x >= 0).ToArray();
        var b1 = nums2.Where(x => x < 0).ToArray();
        b1 = rev(inv(b1));
        var b2 = nums2.Where(x => x >= 0).ToArray();

        var negCount = a1.Length * b2.Length + b1.Length * a2.Length;

        var sign = 1;

        if (k > negCount)
        {
            k -= negCount;
        }
        else
        {
            k = negCount - k + 1;
            var temp = b1;
            b1 = b2;
            b2 = temp;
            sign = -1;
        }


        var lo = 0L;
        var hi = 10000000000L;

        while (lo < hi)
        {
            var mid = (lo + hi) / 2;
            var count = CountProductsSmallerOrEqualToNumber(mid, a1, b1)
             + CountProductsSmallerOrEqualToNumber(mid, a2, b2);
            Console.WriteLine($"guess: {mid}. count: {count}");
            if (count >= k)
            {
                hi = mid;
            }
            else
            {
                lo = mid + 1;
            }
        }
        return sign * lo;
    }

    public long CountProductsSmallerOrEqualToNumber(long number, int[] nums1, int[] nums2)
    {
        var count = 0L;
        var j = nums2.Length - 1;
        for (int i = 0; i < nums1.Length && j >= 0; i++)
        {
            while (j >= 0 && (long)nums1[i] * (long)nums2[j] > number)
            {
                j--;
            }
            count += j + 1;

        }
