#define ll long long
class Solution {
public:

	ll countKaroProducts(vector<int>& a, vector<int>& b, long long mid) {
		ll totalNumbersLessThanMid = 0;
		for (auto el : a) {
			int cnt = 0;

			if (el >= 0) {   //positive number check  number >=0
				ll s = 0, e = b.size() - 1;  //0 to n-1
				while (s <= e) {
					ll m = (s + e) / 2;
					if ((ll)((ll)el * (ll)b[m]) <= mid) {
						cnt = m + 1;  //if number is less then mid then we can say all less no are negetive
						s = m + 1;
					}
					else {
						e = m - 1;
					}
				}

				totalNumbersLessThanMid += cnt;
			}
			else {   //negetive number search
				ll s = 0, e = b.size() - 1;
				while (s <= e) {
					ll m = (s + e) / 2;
					if ((ll)((ll)el * (ll)b[m]) <= mid) {
						cnt = b.size() - m;
						e = m - 1;    //check any elemet left from mid-1 if then include
					}
					else {
						s = m + 1;
					}
				}

				totalNumbersLessThanMid += cnt;
			}
		}

		return totalNumbersLessThanMid;
	}

	long long kthSmallestProduct(vector<int>& nums1, vector<int>& nums2, long long k) {
		ll s = -1e10;
		ll e = 1e10;
		ll ans;
		while (s <= e) { // log(2*10^10)
			ll mid = ((ll)(s + e)) / 2;
			if (countKaroProducts(nums1, nums2, mid) >= k) {
				ans = mid;   
				e = mid - 1;
			}
			else {
				s = mid + 1;  
			}
		}
		return ans;
	}
};
