```
class Solution {
public:
    using ll = long long;
    long long kthSmallestProduct(vector<int>& nums1, vector<int>& nums2, long long k) {
        int n = nums1.size(), m = nums2.size();
        ll lo = -(ll)1e10 - 1, hi = (ll)1e10 + 1;

        int ln = -1;
        for (int i = 0; i < m; ++i) {
            if (nums2[i] < 0) ln = i;
        }

        while (hi - lo > 1) {
            ll mid = (hi + lo) / 2;
            // cnt number of pairs whose product <= mid
            // This is not as simple as addition
            // Because product of two negative integers becomes positive

            ll cnt = 0;
            ll l = 0;

            ll pos = ln + 1, neg = ln;

            // negative in nums1
            while (l < n && nums1[l] < 0) {
                while (pos < m && (ll)nums1[l] * nums2[pos] > mid) pos++;   
                cnt += m - pos;
                while (neg >= 0 && (ll)nums1[l] * nums2[neg] <= mid) neg--;
                cnt += ln - neg;
                ++l;
            }

            neg = 0, pos = m - 1;;

            // positive in nums1
            while (l < n) {
                while (neg <= ln && (ll)nums1[l] * nums2[neg] <= mid) neg++;
                cnt += neg;
                while (pos > ln && (ll)nums1[l] * nums2[pos] > mid) pos--;
                cnt += pos - ln;
                ++l;
            }

            if (cnt < k) lo = mid;
            else hi = mid;
        }

        return hi;
    }
};
