class Solution {
public:
    long long solve(vector<int >& nums1,vector<int >& nums2,long long product)
    {
        long long count=0;
        for(int a:nums1)
        {
            if(a>0)
            {
                int l=0;
                int h=nums2.size()-1;

                int temp=-1;
                while(l<=h)
                {
                int mid = l+(h-l)/2;

                if((long long)a*nums2[mid]<=product)
                {
                    temp = mid;
                    l=mid+1;
                }
                else
                {
                    h=mid-1;
                }
                }
                count += temp+1;
            }
            else
            {
                int l=0;
                int h=nums2.size()-1;
                int temp=nums2.size();
                while(l<=h)
                {
                    int mid = l+(h-l)/2;
                    if((long long)a*nums2[mid]<=product)
                    {
                        temp = mid;
                        h=mid-1;
                    }
                    else
                    {
                        l=mid+1;
                    }
                }
                count += nums2.size()-temp;
            }
        }
        return count;
    }
    long long kthSmallestProduct(vector<int>& nums1, vector<int>& nums2, long long k) 
    {
       long long int low = -1e10;
       long long int high  = 1e10;

        long long ans;
        long long mid;

        while(low<=high)
        {
            mid = low+(high-low)/2;
            if(solve(nums1,nums2,mid)>=k)
            {
                ans = mid;
                high = mid-1;
            }
            else
            {
                low = mid+1;
            }
        }
        return ans;
    }
};

