using ll=long long;
class Solution {
public:
    // This returns TRUE, iff the number of elements in the set(A)*set(B) <= 'val' is >= 'K' 
    bool predicate(ll val, const ll K, vector<int>& A, vector<int>& B){
        ll cnt=0;
        for(auto a:A){
            if(a<0){
                cnt += findNumberOfNumbersLessThanEqualToValInNonIncreasingArray(val,
                                                                                B,a);
            } else if (a==0) {
                if (val>=0) cnt+=B.size();
            } else if (a>0) {
                cnt += findNumberOfNumbersLessThanEqualToValInNonDecreasingArray(val,
                                                                                B,a);
            }
        }
        return cnt>=K;
    }

    int findNumberOfNumbersLessThanEqualToValInNonIncreasingArray(const ll val,
                                                                  vector<int>& B,
                                                                 const int a){
        // find the smallest index for which B[i]<=val
        int n = B.size();
        int lo=0,hi=n-1;
        // if the smallest numebr is > val, then there is no such element
        if((ll)B[hi]*a > val)return 0;
        while(lo!=hi){
            int mid=lo+(hi-lo)/2;
            if((ll)B[mid]*a <= val)hi=mid;
            else lo=mid+1;
        }
        return n-lo;
    }

    int findNumberOfNumbersLessThanEqualToValInNonDecreasingArray(const ll val,
                                                                  vector<int>& B,
                                                                 const int a){
        // find the largest index for which B[i]<=val
        int n = B.size();
        int lo=0,hi=n-1;
        // if the smallest value if > val, then there is no such elements present
        if((ll)B[lo]*a > val) return 0;
        while(lo!=hi){
            int mid=lo+(hi-lo+1)/2;
            if((ll)B[mid]*a <= val)lo=mid;
            else hi=mid-1;
        }
        return lo+1;
    }

    long long kthSmallestProduct(vector<int>& A, vector<int>& B, long long K) {
        // if x=A[i]*B[j], then we need to find the set of 'x'(s) for which number of numbers <= 'x'  will be >= 'K' ans choose smallest in that set(smallest 'x')

        ll lo=-1e10-100;
        ll hi=1e10+100;

        while(lo!=hi){
            ll mid=lo+(hi-lo)/2;
            if(predicate(mid,K,A,B))hi=mid;
            else lo= mid+1;
        }

        // since k <= A.length * B.length as given in the question, Here no need to check whetehr the answer exists or not

        return lo;
    }
};
