typedef long long ll;
#define all(nums) nums.begin(), nums.end()
#define reverse(nums1, nums2) reverse(all(nums1)), reverse(all(nums2))
class Solution {
public:
    inline ll count(vector<int> &nums1, vector<int> &nums2, ll limit) {
        ll result = 0;
        for(int i = 0, j = nums2.size()-1; i < nums1.size(); i++, result += (j+1))
            while(j >= 0 && (ll)nums1[i]*nums2[j] > limit) j--;
        return result;
    }
    tuple<ll, ll, ll, vector<int>, vector<int>> getStats(vector<int> nums) {
        vector<int> pos(upper_bound(all(nums), 0), nums.end()), neg(nums.begin(), lower_bound(all(nums), 0));
        return {neg.size(), std::count(all(nums), 0), pos.size(), neg, pos};
    }
    long long kthSmallestProduct(vector<int>& nums1, vector<int>& nums2, long long k) {
        ll n = nums1.size(), m = nums2.size();
        auto [neg1, zero1, pos1, nums1Neg, nums1Pos] = getStats(nums1);
        auto [neg2, zero2, pos2, nums2Neg, nums2Pos] = getStats(nums2);
        ll negCount = pos1*neg2+pos2*neg1, zeroCount = n*zero2+m*zero1-zero1*zero2, left=-1e10-5, right=1e10+5, mid;
        if(k > negCount) {
            if(k <= negCount+zeroCount) return 0;
            reverse(nums1Neg, nums2Neg);
            left = 1;
            k -= (negCount + zeroCount);
        } else {
            reverse(nums1Pos, nums2Pos);
            right = 0;
            swap(nums2Pos, nums2Neg);
        }
        while(left < right) {
            mid = left + (right-left)/2;
            if(count(nums1Pos, nums2Pos, mid) + count(nums2Neg, nums1Neg, mid) < k) left = mid+1;
            else right = mid;
        }
        return left;
    }
};
