class Solution
{
public:
    long long kthSmallestProduct(vector<int> &nums1, vector<int> &nums2, long long k)
    {
        // 乘积值一定在 [-10000000000, 10000000000]之间.
        // 值的最大范围跨度(MAX-VLAUE-RANGE) = 20000000000

        // 算法复杂度考虑: Log(MAX-VLAUE-RANGE) * |nums1| * log(|nums2|)
        // 因此, 将nums1设定为长度较小的数组
        if (nums1.size() > nums2.size())
        {
            nums1.swap(nums2);
        }

        long long begin = -10000000000ll;
        long long end = 10000000000ll;
        long long result = -1;
        while (begin <= end)
        {
            auto middle = (begin + end) / 2;

            //不大于middle的乘积对的个数
            long long count = 0;
            for (auto &n1 : nums1)
            {
                count += getTotalNotGreaterThan(nums2, n1, middle);
            }
            if (count >= k)
            {
                result = middle;
                end = middle - 1;
            }
            else
            {
                begin = middle + 1;
            }
        }
        return result;
    }

    //在nums2中, 与n1相乘，但是乘积值不大于value的元素个数.
    long long getTotalNotGreaterThan(vector<int> &nums2, long long n1, long long value)
    {
        //可以自己写一个binary search, 我这里套用stl upper_bound/lower_bound方法.
        if (n1 >= 0)
        {
            return distance(nums2.begin(), upper_bound(nums2.begin(), nums2.end(), -1, [=](int v1, int v2)
                                                       { return value < (long)v2 * n1; }));
        }
        else
        {
            n1 *= -1;
            return distance(lower_bound(nums2.begin(), nums2.end(), -1, [=](int v1, int v2)
                                        { return (long)v1 * n1 < -value; }),
                            nums2.end());
        }
    }
};
