		for(int i=0;i<n;i++)
            tree[i] = new ArrayList<>();

	    for(int i=1;i<n;i++)
            tree[parents[i]].add(i);

	    int counts[] = new int[n];
        Arrays.fill(counts,1);

		dfs(0,tree,counts);

		long max = 0, res = 0;
        HashMap<Long,Long> map = new HashMap<>();        

		for(int i=0;i<n;i++){
            long prod = 1;
            for(int u : tree[i]) {
                prod *= counts[u];
            }

            if(i!=0) prod *= n-counts[i];
            map.put(prod,map.getOrDefault(prod,0L)+1L);

            if(prod >= max) {
                max = prod;
                res = map.get(prod);
            }
        }

        return (int)res;
    }

    private int dfs(int node,List<Integer>[] tree,int counts[]){ 

        for(int next : tree[node])
            counts[node] += dfs(next,tree,counts);

	    return counts[node];

    }
}
