Get the number of nodes of L subtree and R subtree. and score = l * r * rest. (if l, r, rest == 0 then set to 1)
```
class Solution {
public:
    long long ans = 0;
    long long cnt = 0;
    vector<vector<int> > adj;
    int n;
    long long dfs(int node) {
        long long l = 0, r = 0;
        if(adj[node].size() >= 1) {
            l = dfs(adj[node][0]);
        }
        if(adj[node].size() >= 2) {
            r = dfs(adj[node][1]);
        }
        long long rest = n - 1 - r - l;
        long long score = (l == 0 ? 1 : l) * (r == 0 ? 1 : r) * (rest == 0 ? 1 : rest);
        if(score == ans) {
            ++cnt;
        }else if(score > ans) {
            ans = score;
            cnt = 1;
        }
        return l + r + 1;
    }
    int countHighestScoreNodes(vector<int>& parents) {
        n = parents.size();
        adj.resize(n);
        for(int i = 1; i < parents.size(); i++ ){
            adj[parents[i]].push_back(i);
        }
        dfs(0);
        return cnt;
    }
};
```

