        for(int i=1;i<n;i++){
            int ele=parents[i];
            if(m[ele]->left==NULL)m[ele]->left=m[i];
            else m[ele]->right=m[i];
        }

        func(m[0],n);

        long long int maxi=0,t=0;
        for(auto ele:ans){
            if(ele.first>=maxi){
                t=ele.second;
                maxi=ele.first;
            }
        }
        return t;
