            int currNodes=dfs(curr,adj,totalNodes);
            sumOfNodes+=currNodes;
            product*=currNodes;
        }
        int upNodes=(totalNodes-sumOfNodes-1);
        if(upNodes>0) product*=upNodes;
        hash.put(product,hash.getOrDefault(product,0)+1);
        return sumOfNodes+1;
