        for(int i=1;i<parents.length;i++)
        {
            adj[parents[i]].add(i);
        }
        hash=new HashMap<>();
        dfs(0,adj,parents.length);
        long max=0;
        for(long i : hash.keySet()) max=Math.max(max,i);
        return hash.get(max);
    }
}

