public class Solution {

    int dfs(int[,] leaves, int[,] nums, int node)
    {
        int left = leaves[node, 0] == -1? 0: dfs(leaves, nums, leaves[node, 0]);
        int right = leaves[node, 1] == -1? 0: dfs(leaves, nums, leaves[node, 1]);
        nums[node, 0] = left;
        nums[node, 1] = right;
        return left + right + 1;
    }

    public int CountHighestScoreNodes(int[] parents) {
        int len = parents.Length;
        if (len < 2)
            return 0;

        int root = 0;
        int[,] nums= new int[len,2];
        int[,] leaves = new int[len, 2];

        for (int i = 0; i < len; i++){
            leaves[i,0] = -1;
            leaves[i,1] = -1;
        }

        for (int i = 0; i < len; i++){
            if (parents[i] == -1){
                root = i;
                continue;
            }

            if (leaves[parents[i], 0] == -1)
                leaves[parents[i], 0] = i;
            else
                leaves[parents[i], 1] = i;
        }

        dfs (leaves, nums, root);

