	// use this function to return the size, however, also keep updating the score simulatneously
	// store the score corresponding to each node in a map called score, declared above, also 
	// keep a note of the largest score so far!

    long long sizeHunt(TreeNode* node,int n)
    {
        if(!node) return 0;
        if(!node->left&&!node->right) // incase of a leaf node
        {
            score[node->val]=n-1;
            maxScore=max(maxScore,(long long) n-1);
            return 1;
        }

		// incase it's a non-leaf node, you need to get the size of the left and right subtree and also, the nodes, other than that.

        long long left=sizeHunt(node->left,n);
        long long right=sizeHunt(node->right,n);

		// remaining calculates the nodes other than the left and right subtrees.

        long long rem=n-(left+right+1);
        long long s=0;
        if(left&&right) s=left*right;
        else if(left) s=left;
        else s=right;

        if(rem) s*=rem;

        score[node->val]=s;
        maxScore=max(maxScore,score[node->val]);

		// return the size of this subtree

        return left+right+1;
    }

    int countHighestScoreNodes(vector<int>& parents) {
        int n=parents.size();
        map<int,TreeNode*> mp;

		// store the node correspoding to the node value in the map

        for(int i=0;i<n;i++)
        {
            TreeNode* node=new TreeNode();
            node->val=i;
            mp[i]=node;
        }

		// use the parent array to assign the children to its parent.

        for(int i=1;i<n;i++)
        {
            int p=parents[i];
            TreeNode* prt=mp[p];
            if(!prt->left) prt->left=mp[i];
            else prt->right=mp[i];

            mp[p]=prt;
        }

        TreeNode *root=mp[0];

        sizeHunt(root,n);
        int ans=0;

		// go through the whole score map to see how many nodes have got the maxScore.

        for(auto it=score.begin();it!=score.end();it++)
        {
            if(it->second==maxScore) ans++;
        }

        return ans;
    }
};

