class Solution {
public:
    int countHighestScoreNodes(vector<int>& parents) {
        int n = parents.size(); 
        vector<vector<int>> tree(n); 
        for (int i = 0; i < n; ++i) 
            if (parents[i] >= 0) tree[parents[i]].push_back(i); 

        map<long, int> freq; 

        function<int(int)> fn = [&](int x) {
            int left = 0, right = 0; 
            if (tree[x].size()) left = fn(tree[x][0]); 
            if (tree[x].size() > 1) right = fn(tree[x][1]); 
            long score = 1; 
            if (left) score *= left; 
            if (right) score *= right; 
            if (n - 1 - left - right) score *= n - 1 - left - right; 
            ++freq[score]; 
            return 1 + left + right; 
        };

        fn(0); 
        return freq.rbegin()->second; 
    }
};
```

