class Solution:
    def countHighestScoreNodes(self, parents: List[int]) -> int:

        sizes = [[0]*2 for _ in range(len(parents))]
        dic = defaultdict(list)

        def rec(root):

            if not dic[root]:
                return 1

            if len(dic[root]) == 1:
                sizes[root][0] = rec(dic[root][0])
            elif len(dic[root]) == 2:
                sizes[root][0] = rec(dic[root][0])
                sizes[root][1] = rec(dic[root][1])

            return 1 + sizes[root][0] + sizes[root][1]

        for i in range(1,len(parents)):
            dic[parents[i]].append(i)

        total = rec(0)

        res = []
        for x,y in sizes:
            res.append(max(x,1) * max(y,1) * max(total-x-y-1,1))

        return res.count(max(res))

