class Solution {
    static class TreeNode {
        int val;
        TreeNode left,right;
        TreeNode() {

        }
        TreeNode(int val) {
            this.val=val;
        }
    }
    public int countHighestScoreNodes(int[] parents) {
        int n=parents.length;
        HashMap<Integer,TreeNode> map=new HashMap<>();
        for(int i=0;i<n;i++) {
            map.put(i,new TreeNode(i));
        }
        TreeNode root=null;
        for(int i=0;i<n;i++) {
            if(parents[i]==-1) {
                root=map.get(i);
            }
            else {
                TreeNode parent=map.get(parents[i]);
                TreeNode child=map.get(i);
                if(parent.left==null) {
                    parent.left=child;
                }
                else {
                    parent.right=child;
                }

            }
        }
        Queue<Long> queue=new LinkedList<>();
        getScore(root,queue,n);
        long max=0;
        int count=0;
        while(n-->0) {
            long temp=queue.poll();
            max=Math.max(max,temp);
            queue.offer(temp);
        }
        while(!queue.isEmpty()) {
            if(max==queue.poll())
                ++count;
        }
        return count;


    }
    long getScore(TreeNode root,Queue<Long> queue,int size) {
        if(root==null)
            return 0;
        long left=getScore(root.left,queue,size);
        long right=getScore(root.right,queue,size);
        long up=size-left-right-1;
        long temp=0;
        if(left==0&&right==0&&up==0) {
            temp=0;
        }
        else {
            long tempLeft=left==0?1:left;
            long tempRight=right==0?1:right;
            up=up==0?1:up;
            temp=tempLeft*tempRight*up;
        }

        queue.offer(temp);
        return 1+left+right;
    }
}

