class Solution {
public:
    int countHighestScoreNodes(vector<int>& p) {
        int n = p.size();
        vector<int> tree[n], subTreeSize(n, 0), vis(n, 0);
        for(int i = 0; i < n; i++){
            if(p[i] != -1){
                tree[p[i]].push_back(i);
            }
        }

        function<int(int)> dfs=[&](int u)-> int{
            vis[u] = 1;
            int currSize = 1;
            for(auto& next : tree[u]){
                if(!vis[next]){
                    currSize += dfs(next);
                }
            }
            subTreeSize[u] = currSize;
            return currSize;
        };

        dfs(0);

        long ans = 0;
        unordered_map<long ,int> freq;
        for(int i = 0; i < n; i++){
            long val = 1, sum = 0;
            for(auto& c : tree[i]){
                if(p[c] == i) val *= subTreeSize[c], sum += subTreeSize[c];
            }
            val *= max(1L, n - sum - 1);
            ans = max(ans, val);
            freq[val]++;
        }

        return freq[ans];
    }
};
