class Solution {
    long max = 0;
    long cnt = 0;
    public int countHighestScoreNodes(int[] parents) {
        TreeNode root = buildTree(parents); 
        dfs(root, parents.length);
        return (int) cnt;
    }

    private long dfs(TreeNode node, int n) {
        if (node == null) return 0;
        long l = dfs(node.left, n);
        long r = dfs(node.right, n);
        long remain = n - l - r - 1;
        long prod = (l > 0 ? l : 1) * (r > 0 ? r : 1) * (remain > 0 ? remain : 1); // tricky
        if (prod == max) {
            cnt++;
        } else if (prod > max) {
            max = prod;
            cnt = 1;
        }
        return l + r + 1;
    }

    private TreeNode buildTree(int[] parents) {
        int n = parents.length;
        TreeNode[] nodeArray = new TreeNode[n];
        TreeNode root = null;

        for (int i = 0; i < n; i++) {
            nodeArray[i] = new TreeNode(i);
        }

        for (int i = 0; i < n; i++) {
            if (parents[i] == -1) {
                root = nodeArray[i];
                continue;
            }
            TreeNode node = nodeArray[parents[i]]; // i = 1, node = 2
            if (node.left == null) {
                node.left = nodeArray[i];
            } else {
                node.right = nodeArray[i];
            }

        }
        return root;
    }
}

class TreeNode {
