class Solution {
public:
    int countHighestScoreNodes(vector<int>& parents) {

        unordered_map<int, vector<int>> graph;

        for (int i = 0; i < parents.size(); ++i)
        {
            int p = parents[i];
            if (graph.find(p) != graph.end())
            {
                graph[p].push_back(i);
            }
            else
            {
                vector<int> v { i };
                graph[p] = v;
            }
        }

        unordered_map<int, long long> productMap;
        unordered_map<int, int> totalMap;

        int sum = dfs(-1, graph, productMap, totalMap);

        long long max = 0;
        int count = 0;
        long long product = 1;

        for (int i = 0; i < parents.size(); ++i)
        {
            product = 1;

            if (i == 0)
            {
                product = productMap[0];
            }
            else
            {
                product = (sum - totalMap[i] - 1) * productMap[i];
            }

            if (product > max)
            {
                max = product;
                count = 1;
            }
            else if (product == max)
            {
                ++count;
            }
        }
        return count;
    }


    int dfs(int node, unordered_map<int, vector<int>> &graph, unordered_map<int, long long> &productMap, 
            unordered_map<int, int> &totalMap)
    {
        int sum = 1;
        long long product = 1;

        if (graph.find(node) == graph.end())
        {
            totalMap[node] = sum;
            productMap[node] = product;

            return sum;
        }

        vector<int> nodes = graph[node];

        for (int &n : nodes)
        {
            int num = dfs(n, graph, productMap, totalMap);

            sum += num;
            product *= num;
        }

        totalMap[node] = sum;
        productMap[node] = product;

        return sum;
    }
};
