class Solution {
public:
    int dfs(int u, vector<vector<int>>& graph,vector<int>& numNodes) {
        numNodes[u] = 1;
        for (int v : graph[u]) {
            numNodes[u] += dfs(v,graph,numNodes);
        }
        return numNodes[u];
    }
    int countHighestScoreNodes(vector<int>& parents) {
        int n = parents.size();

        vector<vector<int>> graph(n);
        for (int i = 1; i < n; i++) {
            graph[parents[i]].push_back(i);
        }

        vector<int> numNodes(n,-1);
        dfs(0,graph,numNodes);

        vector<long long> score(n,0L);
        long long maxScore = LLONG_MIN;
        for (int i = 0; i < n; i++) {
            score[i] = (i > 0) ? (numNodes[0] - numNodes[i]) : 1;
            for (int child : graph[i]) {
                score[i] *= numNodes[child];
            }
            maxScore = max(maxScore,score[i]);
        }

        return count_if(begin(score),end(score),[maxScore](long long x){return x == maxScore;});
    }
};
