class Solution {
    int n;
    ArrayList<Integer>[] tree;
    long[] res;
    public int countHighestScoreNodes(int[] parents) {
        n = parents.length;
        tree = new ArrayList[n];
        for(int i = 0 ; i < n ; i++)tree[i] = new ArrayList<>();

        for(int i = 1 ; i < n ; i++){
            tree[parents[i]].add(i);
        }

        res = new long[n];

        depth(0);
        long max = 0;
        for(long v : res){
             max = Math.max(max,v);
        }
        int count = 0 ;
        for(long v : res) if(v == max) count++;
        return count;
    }


    public int depth(int src){
        int child = 1;
        long ma = 1;
        for(int nbr : tree[src]){
            int sc = depth(nbr);
            ma *= sc;
            child += sc;
        }
        if(src != 0)
            ma *= (n - child);

        res[src] = ma;
        return child;
    }
}
