class Solution {
public:
    int countHighestScoreNodes(vector<int>& parents) {
        int size = parents.size();
        vector<vector<int>> tree (size);
        for (int i = 1; i < size; i+=1) tree[parents[i]].emplace_back(i);

        vector<pair<int, int>> mapping_nodes (size);
        function<int(int)> dfs_count_left_right_node = [&] (int root) {
            int cnt[] {0, 0};
            for (int i = 0; i < tree[root].size(); i+=1)
                cnt[i] = dfs_count_left_right_node(tree[root][i]);
            mapping_nodes[root].first = cnt[0],  mapping_nodes[root].second = cnt[1];
            return 1 + cnt[0] + cnt[1];
