class Solution:
    def countHighestScoreNodes(self, parents: List[int]) -> int:
        n = len(parents)

        cntChilds = [0 for _ in range(n)]
        # buld graph
        graph = [[] for _ in range(n)]
        for i in range(1,n):
            graph[parents[i]].append(i)
        ##
        countChilds = [0 for _ in range(n)]
        def countChildNodes(root):
            if root == None:
                return 0
            if len(graph[root]) >= 1:
                countChilds[root] += countChildNodes(graph[root][0])
            if len(graph[root]) == 2:
                countChilds[root] += countChildNodes(graph[root][1])
            return countChilds[root] + 1
        countChildNodes(0)

        ansMax = 0
        ans = 0
        for i in range(n):
            numLeft = numRight = 0
            if len(graph[i]) >= 1:
                numLeft = countChilds[graph[i][0]] + 1
            if len(graph[i]) == 2:
                numRight = countChilds[graph[i][1]] + 1
            numRestOf = max(1, n - 1 - numLeft - numRight)
            numLeft = max(1, numLeft)
            numRight = max(1, numRight)
            currCnt = numRestOf * numLeft * numRight
            if currCnt > ansMax:
                ansMax = currCnt
                ans = 1
            elif currCnt == ansMax:
                ans += 1
        return ans
