class Solution {
public:

    int solve(vector<int>&parents, vector<int>adj[], unordered_map<int,pair<int,int>>&x, int node) {

        int left=0;
        int right=0;
        int size = adj[node].size();

        if(x.find(node)!=x.end()) return x[node].first+x[node].second;

        if(size==0) {
            x[node]={0,0};
        }
        else if(size==1){
            left = solve(parents,adj,x,adj[node][0]);
            x[node]={left,0};
        }
        else if(size==2){
            left = solve(parents,adj,x,adj[node][0]);
            right = solve(parents,adj,x,adj[node][1]);
            x[node]={left,right};
        }

        return left+right+1;
    }

    int countHighestScoreNodes(vector<int>& parents) {
        int n=parents.size();

        // Stores node -> {count of left Subtree nodes, count of right subtree nodes}
        unordered_map<int,pair<int,int>> x;
        vector<int> adj[n];

        // Making directed Graph
        for(int i=0;i<n;i++){
            if(parents[i]!=-1) adj[parents[i]].push_back(i);
        }    

        for(int i=0;i<n;i++){
          if(x.find(i)==x.end()) solve(parents,adj,x,i);
        }

        unordered_map<long long,long long> scores;
        long long ans=INT_MIN;

        for(int i=0;i<n;i++) {      
              long long curScore=1;
              int size=adj[i].size();

              long long sumLeftNode = x[i].first; // left subtree nodes
              long long sumRightNode = x[i].second; // right subtree nodes
              long long parent = n-1-sumLeftNode-sumRightNode; // parent nodes

              if(size==0){
                  sumLeftNode=1;
                  sumRightNode=1;
                  if(parent<=0) parent=1;
              }

              else if(size==1){
                  if(sumLeftNode<=0) sumLeftNode=1;
                  else if(sumRightNode<=0) sumRightNode=1;

                  if(parent<=0) parent=1;
              }

              else if(size==2){
                  if(parent<=0) parent=1;
              }

              ans=max(ans,parent*sumLeftNode*sumRightNode);
              curScore=parent*sumLeftNode*sumRightNode;
              scores[curScore]++;
      }
      return scores[ans];
    }
};
```

