class Solution {
public:
    int dfs(int cn,int n,vector<vector<int>> &v,vector<long long> &sub){
        long long x=1,y=0;
        for(auto&ch:v[cn]){
            long long z=dfs(ch,n,v,sub);
            x*=max(1ll,z);
            y+=z;
        }
        x*=max(1ll,n-1-y);
        sub[cn]=x;
