            adj[parents[i]].push_back(i);
        }
    }
    int numberOfNode(int node=0,int par=-1){
        int cnt=0;
        for(auto it:adj[node]){
            if(it==par) continue;
            cnt+=numberOfNode(it,node);
        }
        return cnt+1;
    }
    void childCount(int node=0,int par=-1){
        for(auto it:adj[node]){
            if(it==par) continue;
            childCount(it,node);
            dp[node]+=dp[it];
        }
        dp[node]+=1;
    }
    int countHighestScoreNodes(vector<int>& parents) {
        int cnt = 0;
        treeConstruct(parents);
        int noOfNode = numberOfNode();
        childCount();
        map<long long,int> mp;
        for(int i=0;i<parents.size();i++){
            vector<int> temp;
            long long amit = 1;
            for(auto it:adj[i]) if(dp[i]>dp[it]) temp.push_back(it);
            int total = 1;
            for(auto it:temp) total+=dp[it];
            int rem = noOfNode-total;
            for(auto it:temp) if(dp[it]!=0) amit*=dp[it];
            if(rem!=0) amit*=rem;
            mp[amit]++;
        }
