Construct the array of children out of the array of parents, then dfs through the tree keeping track of the number of nodes in the subtrees of the children

- Time complexity: O(n)
- Space complexity: O(n)

```
class Solution {
public:
    vector<pair<int, int>> children;
    size_t maxScore{ 0 };
    size_t maxScoreNodesCnt{ 0 };

    size_t FillScore(int node)
    {
        if (node == -1)
            return 0;

        size_t lTreeNodes = FillScore(children[node].first);
        size_t rTreeNodes = FillScore(children[node].second);
        size_t outerTreeNodes = children.size() - 1 - lTreeNodes - rTreeNodes;

        size_t score = max(lTreeNodes, 1ul) * max(rTreeNodes, 1ul) * max(outerTreeNodes, 1ul);        
        if (score >= maxScore)
        {
            maxScoreNodesCnt = score <= maxScore? maxScoreNodesCnt + 1 : 1;
            maxScore = max(score, maxScore);
        }

        return lTreeNodes + rTreeNodes + 1;
    }

    int countHighestScoreNodes(vector<int>& parents) 
    {
        children.resize(parents.size(), make_pair(-1, -1));
        for (int node = 1; node < parents.size(); ++node)
        {
            int parent = parents[node];
            int& child = children[parent].first == -1?
                children[parent].first :
                children[parent].second;

            child = node;
        }

        FillScore(0);
        return maxScoreNodesCnt;
    }
};
```

