typedef long long ll;

class Solution {
private:
    vector<vector<int>> adj;
    vector<ll> scores;
    int n;
    ll maxScore;
public:
    int countAndScore(int u) {
        int c = 1;
        ll score = 1;
        for(auto v : adj[u]) {
            int size = countAndScore(v);
            score *= size;
            c+=size;
        }
        scores[u] = u == 0 ? score : score*(n-c);
        maxScore = max(maxScore, scores[u]);

        return c;
    }

    int countHighestScoreNodes(vector<int>& p) {
        n = p.size();
        adj.resize(n);
        scores.resize(n, 1);

        for(int i=1; i<n; i++) 
            adj[p[i]].push_back(i);

        maxScore = INT_MIN;
        countAndScore(0);

        int res = 0;
        for(int i=0; i<n; i++){
            res += (scores[i] == maxScore) ? 1 : 0;
        }

        return res;
    }
};
