class Solution {
    public int countHighestScoreNodes(int[] parents) {
        HashMap<Integer, List<Integer>> map = new HashMap<>();
        int n = parents.length;

        for(int i =0; i < n; i++){
            map.put(i, new ArrayList<>());
        }

        for(int i = 1; i < n; i++){
            map.get(parents[i]).add(i);
        }

        int[] count = new int[n];
        int x = 1;
        for(int i = 0; i < n; i++){
            if(count[i] == 0){
                x = find_count(i, map, count);
            }
        }

        long max=0;int ans=0;

        for(int i = 0; i < n; i++){
            List<Integer> child = map.get(i);
            long curr = 1;

            if(child != null){
                for(int j = 0; j < child.size(); j++){
                    curr = curr * (long)count[child.get(j)];
                }
            }

            if(parents[i] != -1){
                curr = curr * (long)(n - count[i]);
            }

            if(curr > max){
                max = curr;
                ans = 1;
            }
            else if(curr == max) ans++;
        }

        return ans;

    }

    int find_count(int node, HashMap<Integer, List<Integer>> map, int[] count){
        if(count[node] != 0) return count[node];

        List<Integer> child = map.get(node);

        if(child == null){
            return count[node] = 1;
        }
        int cnt = 1;

        for(int i = 0; i < child.size(); i++){
            cnt = cnt + find_count(child.get(i), map, count);
        }

        count[node] = cnt;
        return cnt;
    }


}






