# Approach
Based on hints by LeetCode:
> For each node, you need to find the sizes of the subtrees rooted in each of its children.

> How to determine the number of nodes in the rest of the tree? Can you subtract the size of the subtree rooted at the node from the total number of nodes of the tree?

# Code
```
// implicitly imported by LeetCode
// type TreeNode struct {
//	Val   int
//	Left  *TreeNode
//	Right *TreeNode
//}

// Score holds number of nodes in left and right subtrees
type Score struct {
	Left  int
	Right int
}

func countHighestScoreNodes(parents []int) int {
	n := len(parents)
	// map of node value to itself for creating tree purpose
	nodes := make(map[int]*TreeNode, n)
	nodes[0] = &TreeNode{Val: 0}

	for i := 1; i < n; i++ {
		makeNode(parents, nodes, i)
	}

	scores := make(map[int]Score, n)
	score(nodes[0], scores)

	max := 0
	amount := 0
	for _, nodeScore := range scores {
		// adjust multipliers
		others := 1
		if n-nodeScore.Left-nodeScore.Right-1 != 0 {
			others = n - nodeScore.Left - nodeScore.Right - 1
		}
		if nodeScore.Left == 0 {
			nodeScore.Left = 1
		}
		if nodeScore.Right == 0 {
			nodeScore.Right = 1
		}

		total := nodeScore.Left * nodeScore.Right * others
		if total > max {
			max = total
			amount = 1
		} else if total == max {
			amount++
		}
	}
	return amount
}

func makeNode(parents []int, nodes map[int]*TreeNode, index int) {
	// quit if node already exists
    if nodes[index] != nil {
        return
    }

	node := &TreeNode{Val: index}
	nodes[index] = node

	// to link freshly created node to a parent, parent must be presented
	if parent, ok := nodes[parents[index]]; ok {
		assignChild(parent, node)
	} else { // create one if it doesn't exist
		makeNode(parents, nodes, parents[index])
		assignChild(nodes[parents[index]], node)
	}
}

// links child to parent. Left or Right doesn't matter
func assignChild(parent, child *TreeNode) {
	if parent.Left == nil {
		parent.Left = child
	} else {
		parent.Right = child
	}
}

// calculates score for a node
func score(node *TreeNode, scores map[int]Score) int {
	if node == nil {
		return 0
	}
	left := score(node.Left, scores)
	right := score(node.Right, scores)
	scores[node.Val] = Score{left, right}
	// return score of the node +1 to include node as well
	return left + right + 1
}

```

