class Solution {

    static long score = Integer.MIN_VALUE ; 
    static int count = 0 ; 


    public int countHighestScoreNodes(int[] parents) {

        // basically for this. we  will have to travers in the tree ; from up to down , 
        // but we have given parent array , so frist we will have to convert  parents 
        //  array to hashamap 

           score = Integer.MIN_VALUE ;  
           count = 0 ; 

        HashMap<Integer,ArrayList<Integer>> map = new HashMap<>() ; 


        for( int i = 0 ; i < parents.length ; i++)
        {
            if( map.containsKey(parents[i]))
            {
               ArrayList<Integer> temp =  map.get(parents[i]) ; 
               temp.add(i) ; 
            }
            else
            {
                ArrayList<Integer> temp =  new ArrayList<>() ; 
                temp.add(i) ; 
                map.put(parents[i] , temp) ; 
            }

        }


        // now we have stored , all details of node and children in hashmap ;


         // now we will need two hashmap , one is left and one is right ; 
         // LEFT HASHMAP , will store the length of left subtree ; 
         // Right Hashmap , will store the lnength of right Subtree ; 

            HashMap<Integer,Integer> left = new HashMap<>() ; 
            HashMap<Integer,Integer> right = new HashMap<>() ; 

            int totalLength = helper(0 , map , left , right) ; 


            // now we will find answer , either we can use static variable or , just returning result

            // System.out.println( "total Length " + totalLength) ; 

             findAnswer( 0 , map , left , right , totalLength) ; 

             return count ; 

    }
    public int helper( int vertex , HashMap<Integer,ArrayList<Integer>> map , HashMap<Integer,Integer> left , HashMap<Integer,Integer> right)
    {

         int length = 1 ; 

          if( map.containsKey(vertex) == false)
          return length ; 



          ArrayList<Integer> list = map.get(vertex) ; 

          int leftLength = helper(list.get(0) , map , left ,right) ; 
            length += leftLength ; 

          left.put(vertex , leftLength) ; 

          if( list.size() == 1 )
          return length ; 


          int rightLength = helper(list.get(1) , map  , left , right) ; 

          right.put(vertex , rightLength) ; 

          length += rightLength ; 


          return length ; 


    }
    public void findAnswer(int vertex , HashMap<Integer,ArrayList<Integer>> map , HashMap<Integer,Integer> left , HashMap<Integer,Integer> right , int totalLength)
    {

         int option1 = 0 ; 
         int option2 = 0 ; 
         int option3 = 0 ; 



         if(left.containsKey(vertex))
         option1 = left.get(vertex) ; 



         if(right.containsKey(vertex))
         option2 = right.get(vertex) ; 



         option3 = totalLength - 1 - option1 - option2 ; 

        // System.out.println( "vertex = " + vertex + " " + option1 + " " + option2+ " " + option3) ; 

         long answer = 1 ; 

        if(option3 != 0 )
        answer *= (long)option3 ; 

        if( option1 != 0 )
        answer *= (long)option1 ;

        if( option2 != 0 )
        answer *= (long)option2 ; 

           if( answer == score)
            count++ ;
           else if( answer > score)
            {
                score = answer ; 
                count = 1 ; 
            }

        if(map.containsKey(vertex) == false)
        {
            return  ; 
        }

        ArrayList<Integer> temp = map.get(vertex) ;

        findAnswer(temp.get(0) , map , left , right , totalLength) ; 



         if( temp.size() == 1)
         return ; 

        findAnswer(temp.get(1) , map , left , right , totalLength)  ;


    }
}
