class Solution {
public:
    vector<vector<int>> g;
    vector<long long> sub,ans;
    long long n;
    void dfs(int u,int par){
        int t = 1,f=0;
        for(auto v:g[u]){
            if(v==par)continue;
            dfs(v,u);
            f = 1;
            sub[u]+=sub[v];
            t=t*sub[v];
        }
        if(!f){
            ans[u] = n-1;
            return;
        }
        ans[u] = t;
        if(n-sub[u]>0){
            ans[u]=t*(n-sub[u]);
        }
