        for(int i=1;i<n;i++){
            g[parents[i]].push_back(i);
            g[i].push_back(parents[i]);
        }
        dfs(0,-1);
        int cnt = 0;
       sort(ans.begin(),ans.end());
        for(int i=0;i<n;i++){
            if(ans[n-1]==ans[i]){
                cnt++;
            }
        }
        return cnt;
    }
};
