class Solution {
    long Ans = 0;
    HashMap<Long,Integer> map = new HashMap();
    public int countHighestScoreNodes(int[] parents) {
        int children[][] = new int[parents.length][2];
        for(int child[]: children){
            child[0] = -1;
            child[1] = -1;
        }
        int startNode=0;
        for(int i=0; i<parents.length; i++){
            if(parents[i]==-1) startNode = i;
            else{
                if(children[parents[i]][0]==-1){
                    children[parents[i]][0]=i;
                }
                else{
                    children[parents[i]][1]=i;
                }
            }
        }

        DFS(startNode, children);
        return map.get(Ans);
    }
    int DFS(int node, int[][]child){
        if(node==-1) return 0;
        int l = DFS(child[node][0], child);
        int r = DFS(child[node][1], child);
        int remain = child.length-1-l-r;
        long ans = 1;
        if(l!=0){
            ans = ans*l;
        }
        if(r!=0){
            ans = ans*r;
        }
        if(remain!=0){
            ans=ans*remain;
        }
        map.put(ans, map.getOrDefault(ans,0)+1);
        Ans = Math.max(Ans,ans);
        return l+r+1;
    }
}
