struct Helper {
  g: Vec<Vec<usize>>,
  memo: Vec<usize>,
}

impl Helper {
  fn dfs(&mut self, ci:usize, li:usize) -> usize {
    let n = self.g.len();
    let mut result = 1usize;
    let mut num = 0;
    for i in 0..self.g[ci].len() {
      let ni = self.g[ci][i];
      if ni == li { continue }

      let v = self.dfs(ni, ci);
      result *= v;
      num += v;
    }

    let left = n - num - 1;
    let result = if left == 0 {
      result
    } else {
      result * left
    };

    self.memo[ci] = result;
    num + 1
  }
}

impl Solution {
  pub fn count_highest_score_nodes(parents: Vec<i32>) -> i32 {
    let n = parents.len();
    let mut g = vec![vec![];n];

    for i in 1..n {
      let ni = parents[i] as usize;
      g[ni].push(i);
      g[i].push(ni);
    }

    let mut helper = Helper { g, memo:vec![0;n] };
    helper.dfs(0, 10000000);

    let mut max = 0;
    for i in 0..n {
      max = max.max(helper.memo[i]);
    }

    let mut result = 0;
    for i in 0..n {
      if max == helper.memo[i] {
        result += 1;
      }
    }
    result
  }
}
