class Solution {
    public int countHighestScoreNodes(int[] parents) {
        long[] arr = {Long.MIN_VALUE, 1};
        findScore(buildTree(parents), parents.length, arr);
        return (int) arr[1];
    }

    public long findScore(TreeNode node, int total, long[] arr) {
        if (node == null) return 0;
        long leftCount = findScore(node.left, total, arr);
        long rightCount = findScore(node.right, total, arr);
        long score = Math.max(1, leftCount) * Math.max(1, rightCount) *
                Math.max((total - (leftCount + rightCount + 1)), 1);
        if (score == arr[0]) arr[1]++;
        else if (score > arr[0]) {
            arr[0] = score;
            arr[1] = 1;
        }
        return leftCount + rightCount + 1;
    }

    public TreeNode buildTree(int[] parents) {
        HashMap<Integer, TreeNode> map = new HashMap<>();
        for (int i = 0; i < parents.length; i++) {
            map.put(i, new TreeNode(i));
        }
        for (int i = 0; i < parents.length; i++) {
            if (i == 0) continue;
            TreeNode node = map.get(i);
            if (map.get(parents[i]).left == null) map.get(parents[i]).left = node;
            else map.get(parents[i]).right = node;
        }
        return map.get(0);
    }
}
class TreeNode {
    int val;
    TreeNode left;
    TreeNode right;

