class Solution {
public:
    int dfs(int root, unordered_map<int,int>& size,  vector<vector<int>>& adj)
    {
        if(adj[root].size()==0)
        {
            return size[root] = 1;
        }

        int s = 0;
        for(int i=0; i<adj[root].size(); i++)
        {
            s = s+dfs(adj[root][i], size, adj);
        }
        return size[root] = 1+s; 
    }
    int countHighestScoreNodes(vector<int>& parents) 
    {
        int n = parents.size();
        int root = 0;
        vector<vector<int>> adj(n);//adjacency list
        for(int i=0; i<n; i++)
        {
            if(parents[i]==-1) root = i;
            else
            {
                adj[parents[i]].push_back(i);
            }
        }

        unordered_map<int,int> size;//containing size of subtrees of each node
        dfs(root, size, adj);

        vector<pair<long long, long long>> m;//scores of each node
        for(int i=0; i<n; i++)
        {
            long long int lc=0, rc=0, par=0;//size contributed by left child, right child, parent
            int f = 0;
            for(int k=0; k<adj[i].size(); k++)
            {
                if(f==0)
                {
                    lc = size[adj[i][k]];
                    f=1;
                }
                else rc = size[adj[i][k]];
            }
            par = n-(1+lc+rc);
            if(lc==0) lc=1;
            if(rc==0) rc=1;
            if(par==0) par=1;

            long long int ans = lc*rc*par;
            m.push_back({ans, i});
        }
        long long int mx = INT_MIN;
        for(int i=0; i<m.size(); i++)
        {
            mx = max(mx, m[i].first);
        }
        int res = 0;
        for(auto pr:m)
        {
            if(pr.first==mx) res++;
        }
        return res;
    }
};
```

