class Solution
{
    public:
        vector<long long> dp;
    void dfs(int node, vector<vector < int>> &tree)
    {
        dp[node] = 1;
        for (auto &child: tree[node])
        {
            dfs(child, tree);
            dp[node] += dp[child];
        }
    }
    int countHighestScoreNodes(vector<int> &parents)
    {
        int n = parents.size();
        vector<vector < int>> tree(n);
        for (int i = 1; i < n; i++)
        {
            tree[parents[i]].push_back(i);
        }
        dp.resize(n, 0);
        dfs(0, tree);
        long long maxi = 0;
        unordered_map < long long, long long > mp;
        for (int i = 0; i < n; i++)
        {
            long long tot = dp[0];
            long long prod = 1;
            long long upperPart = tot - dp[i];
            if (upperPart == 0) upperPart = 1;
            long long downward = 1;
            prod *= upperPart;
            for (auto &child: tree[i])
            {
                downward *= dp[child];
            }
            prod *= downward;
            mp[prod]++;
        }
        maxi = 0;
        for (auto &it: mp)
        {
            if (it.first > maxi) maxi = it.first;
        }
        return mp[maxi];
    }
};
