class Solution {
public:
    int n;
    int solve(int node,unordered_map<int,vector<int>>&tree, map<long,long>&freq){

        vector<int>childs(2,0);

        for(int i=0;i<tree[node].size();i++){
            childs[i] = solve(tree[node][i],tree,freq);
        }

        long left = childs[0], right = childs[1];
        long score=0;
        long total = left+right+1;

        //calculate score
        score= (left==0?1:left) * (right==0?1:right) * (n==total?1:n-total);

        freq[score]++;

        return left+right+1;

    }

    int countHighestScoreNodes(vector<int>& parents) {
        n = parents.size();
