class Solution {
public:
    vector<int> trees[100001];
    int subordinates[100001];
    long long score[100001];
    // Checking for Leaf Node
    bool isLeaf(int src, int par){
        for(auto child : trees[src]){
            if(child != par) return false;
        }return true;
    }
    // Calculating Number of Nodes in a particular subtree i.e. number of subordinates
    void evalSubordinates(int src, int par){
        subordinates[src] = 1;
        score[src] = 1;
        if(isLeaf(src,par)) return;
        for(auto child : trees[src]){
            if(child == par) continue;
            evalSubordinates(child,src);
            subordinates[src] += subordinates[child];
            // children's contribution in the score
            score[src] *= subordinates[child];
        }
    }
    int countHighestScoreNodes(vector<int>& parents) {
        int n = parents.size();
        for(int i=0;i<n;i++){
            if(parents[i] != -1) trees[parents[i]].push_back(i);
            trees[i].push_back(parents[i]);
        }
        evalSubordinates(0,-1);
        long long maxi = score[0], ans = 0;
        // Parent's contribution in the score
        for(int i=1;i<n;i++) {score[i] *= (n-subordinates[i]); maxi = max(maxi, score[i]);}
        for(int i=0;i<n;i++) if(score[i] == maxi) ans++; 
        return ans;
    }
};
```

