from collections import defaultdict
from functools import cache
class Solution:
    def countHighestScoreNodes(self, parents: List[int]) -> int:
        G = defaultdict(list)
        for v, w in enumerate(parents):
            if w == -1: continue
            G[v].append(w)
            G[w].append(v)
        @cache
        def dfs(u, v):
            res = 1
            for w in G[v]:
                if w == u: continue
                res += dfs(v, w)
            return res
        res = 0
        curmax = 0
        for v in range(len(parents)):
            cur = 1
            for w in G[v]:
                cur *= dfs(v,w)
            if cur > curmax:
                res = 1
                curmax = cur
            elif cur == curmax:
                res += 1
        return res



