#define ll long long

class Solution {
public:
    ll n = 0;
    ll maxi = LLONG_MIN;
    ll res = 0;

    ll solve(int node, vector<int> adj[], int par) {

        ll r = 0;
        ll tNodes = 1;
        ll curRes = 1;

        for(auto child : adj[node]) {
            if(par == child) {
                continue;
            }
            // r is the number of nodes from a specific subtree so we immediately include it in 
            // or answer and we also require the number of nodes in the subtree of a particular node
            // so we maintain a tNodes variable
            r = solve(child, adj, node);
            curRes *= r;
            tNodes += r;
        }

        //Lead Node

        if(r == 0) {
            curRes *= (n - tNodes);
            if(curRes > maxi) {
                maxi = curRes;
                res = 1;
            }
            else if(curRes == maxi) {
                res++;
            }
            return tNodes;
        }
        // Generic Node
        else {
            // Handling case for the root node as n-tNodes becomes 0 for it
            curRes *= (n - tNodes) == 0 ? 1 : (n - tNodes);
            if(curRes > maxi) {
                maxi = curRes;
                res = 1;
            }
            else if(curRes == maxi) {
                res++;
            }
            return tNodes;
        }
    }

    int countHighestScoreNodes(vector<int>& parents) {
        n = parents.size();
        vector<int> adj[n];

        for(int i=0; i<parents.size(); i++) {
            if(parents[i] != -1) {
                adj[i].push_back(parents[i]);
                adj[parents[i]].push_back(i);
            }
        }
        solve(0, adj, -1);
        return res;
    }
};
