class Solution {
    unordered_map<int, vector<int>> children;
    int count_nodes(int node) {
        if(nodes[node] != 0)
            return nodes[node];
        const auto &ch = children[node];
        if(ch.empty())
            return 1;
        int ret = 1;
        for(const int n : ch) {
            ret += count_nodes(n);
        }
        return ret;
    }
    vector<int> nodes;
public:
    int countHighestScoreNodes(vector<int>& parents) {
        const int N = parents.size();

        for(int i = 1; i < N; ++i) {
            children[parents[i]].push_back(i);
        }
        nodes.resize(N, 0);
        for(int i = 0; i < N; ++i) {
            nodes[i] = count_nodes(i);
        }

        int ans = 1;
        long long int maxn = 0;

        for(int i = 0; i < N; ++i) {
            long long int score = N - 1;
            if(nodes[i] != 1) {
                long long int left = (N != nodes[i]) ? N - nodes[i] : 1;
                for(const int n : children[i]) {
                    left *= nodes[n];
                }
                score = left;
            }
            if(maxn <= score) {
                if(maxn < score) {
                    ans = 1;
                    maxn = score;
                } else
                    ++ans;
            }
        }

        return ans;
    }
};
