class Solution {
    int n;
    vector<vector<int>> graph;
    vector<int> sub;
    vector<long> ans; /*no of nodes in subGraph*/  

    void dfs(int node,int par = -1){
        sub[node] = 1;
        for(auto it: graph[node]){
            dfs(it,node);
            sub[node]+=sub[it]; 
        }
