public:
    int countHighestScoreNodes(vector<int>& parents) {
        n = parents.size();
        sub.resize(n);  graph.resize(n);
        for(int i = 1;i<n;i++) graph[parents[i]].push_back(i);

        dfs(0);

        for(int i = 0;i<n;i++) {
            long x = sub[0]-sub[i]; if(!x) x = 1; /* This is for case i = 0*/
            for(auto it:graph[i]) x *= long(sub[it]);
            ans.push_back(x);
        }

        return count(ans.begin(),ans.end(),*max_element(ans.begin(),ans.end()));
    }
};
