```go
func countHighestScoreNodes(parents []int) int {
  adjacencyList := make([][]int, len(parents))
  for node, parent := range parents {
    if parent == -1 {
      continue
    }
    adjacencyList[parent] = append(adjacencyList[parent], node)
  }
  scores := make([]int, len(parents))
  dfs(adjacencyList, 0, len(parents), scores)
  var maxScore, count int
  for _, score := range scores {
    if score > maxScore {
      maxScore = score
      count = 1
    } else if score == maxScore {
      count++
    }
  }
  return count
}

// dfs runs a depth first search and computes the score of the current node
func dfs(adjacencyList [][]int, node int, n int, scores []int) int {
  var leftNodes, rightNodes int
  if len(adjacencyList[node]) > 0 {
    leftNodes = dfs(adjacencyList, adjacencyList[node][0], n, scores)
  }
  if len(adjacencyList[node]) > 1 {
    rightNodes = dfs(adjacencyList, adjacencyList[node][1], n, scores)
  }
  curr := 1 + leftNodes + rightNodes // nodes in the current subtree
  // There can be up to 3 subtrees if we remove the current node
  // 1. Part of the parent: number of nodes = n - current subtree's nodes
  // 2. Part of the left subtree
  // 3. Part of the right subtree
  score := max(1, n - curr) * max(1, leftNodes) * max(1, rightNodes)
  // fmt.Printf("Score: %d\n", score)
  scores[node] = score
  return curr
}

func max(a, b int) int {
  if a > b {
    return a
  }
  return b
}
