        if(childs[curr_node].size()){
            for(auto it : childs[curr_node]){
                child_cnt[curr_node] += find_cnt(childs, it, child_cnt);
            }
        } 
        return child_cnt[curr_node];
    }
    int countHighestScoreNodes(vector<int>& parents) {
        int n = parents.size();
        vector<vector<int>> childs(n);
        vector<int> child_cnt(n, 1);
        for(int i = 1; i < parents.size(); ++i){
            childs[parents[i]].push_back(i);
        }
        int curr_node = 0;
        find_cnt(childs, curr_node, child_cnt);
        long long mx = 0;
        int mx_cnt = 0;
        long long cnt_l, cnt_r, cnt_u;
        for(int i = 0; i < parents.size(); ++i){
            if(childs[i].size() == 2){
                cnt_l = child_cnt[childs[i][0]];
                cnt_r = child_cnt[childs[i][1]];
                cnt_u = n - cnt_l - cnt_r;
                cnt_u -= (i ? 1 : 0);
                if(cnt_l * cnt_r * cnt_u > mx){
                    mx = cnt_l * cnt_r * cnt_u;
                    mx_cnt = 1;
